function setupCopyButtons() {
    $(".btn-copy-code-container").each(function () {
        var a = $(this).children(".btn.btn-copy-code").first(), b = $(this).siblings("code").first().get(0),
            c = getCopyText(b);
        "" === c ? $(this).hide() : a.click(function () {
            copyText(b)
        })
    })
}
function copyText(a) {
    var b = getCopyText(a), c = document.createElement("textarea"), d = document.createTextNode(b);
    c.appendChild(d), document.body.appendChild(c);
    try {
        c.select(), document.execCommand("copy"), $(a).animate({opacity: "0.5"}, 1), $(a).animate({opacity: "1.0"}, 500)
    } catch (e) {
        console.log(e.message), alert(e.message)
    }
    document.body.removeChild(c)
}
function getCopyText(a) {
    if ("false" === a.getAttribute("copy"))return "";
    var b = a.cloneNode(!0);
    try {
        for (var c = b.getElementsByTagName("code"), d = c.length - 1; d >= 0; d--) {
            var e = c[d];
            "false" === e.getAttribute("copy") && (e.textContent = "")
        }
    } catch (f) {
    }
    return b.textContent
}
$(document).ready(function () {
    setupCopyButtons()
});